    public boolean validateData() {
        try {
            Float.parseFloat(textFieldFall.getText());
            Float.parseFloat(textFieldFallSlope.getText());
            Float.parseFloat(textFieldFallDuration.getText());
            Float.parseFloat(textFieldMinimum.getText());
            Float.parseFloat(textFieldRiseDuration.getText());
            Float.parseFloat(textFieldRiseSlope.getText());
        } catch (NumberFormatException ex) {
            return false;
        }
        return true;
    }

